//styling
import './Intro.css';

import React from 'react';


function Intro() {

    return (
        <div className='intro'>
            <div className='signup'>
                <p className='main-points white-text'>
                    Building the Next Generation of Index Management
                </p>

                <p className='bio white-text'>
                    Mira Finance is building a DeFi index fund platform that makes crypto investing simple. 
                    Mira's indices give investors exposure to the Aptos ecosystem and will soon launch indices with assets on other blockchain networks. 
                    As Mira evolves, it will grow to become a platform where investors and investment managers can create their own indices that others can see as they test strategies, research, and learn how to invest. 
                    This will create the foundation for Mira as an onramp to other DeFi products on Aptos, including but not limited to: 
                    staking, swap farming, liquidity farming, auto-investing, strategy trading, slot auctions, mining pools, and launchpads
                </p>

                <div className='signup-form'>

                </div>

            </div>
        </div>
    );
}

export default Intro;
